import React, { useState } from "react";

export default function ManagerDashboard() {
  const [requests, setRequests] = useState([
    {
      id: 1,
      category: "Meal",
      amount: "1.5 OMR",
      date: "19-07",
      receipt: "receipt1.png",
      status: "",
      notes: ""
    }
  ]);

  const handleChange = (index, field, value) => {
    const updated = [...requests];
    updated[index][field] = value;
    setRequests(updated);
  };

  const handleSave = () => {
    console.log("Saved Data:", requests);
    alert("✅ Changes saved & notification sent to employee");
  };

  return (
    <div className="#4cbOaf"> 
      <h1 className="text-2xl font-bold mb-4">Manager Dashboard</h1>

      <table className="min-w-full border border-gray-300 rounded-lg overflow-hidden shadow-md">
        <thead className="bg-gray-200">
          <tr>
            <th className="p-2 border">Request ID</th>
            <th className="p-2 border">Category</th>
            <th className="p-2 border">Amount</th>
            <th className="p-2 border">Date</th>
            <th className="p-2 border">Receipt</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Notes</th>
          </tr>
        </thead>
        <tbody>
          {requests.map((req, index) => (
            <tr key={req.id} className="text-center">
              <td className="p-2 border">{req.id}</td>
              <td className="p-2 border">{req.category}</td>
              <td className="p-2 border">{req.amount}</td>
              <td className="p-2 border">{req.date}</td>
              <td className="p-2 border">
                <button className="px-3 py-1 bg-orange-400 text-white rounded">
                  View
                </button>
              </td>
              <td className="p-2 border">
                <select
                  value={req.status}
                  onChange={(e) =>
                    handleChange(index, "status", e.target.value)
                  }
                  className="border rounded px-2 py-1"
                >
                  <option value="">Select</option>
                  <option value="approved">Approved</option>
                  <option value="rejected">Rejected</option>
                </select>
              </td>
              <td className="p-2 border">
                <input
                  type="text"
                  value={req.notes}
                  onChange={(e) =>
                    handleChange(index, "notes", e.target.value)
                  }
                  className="border rounded px-2 py-1 w-full"
                  placeholder="Add note..."
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button
        onClick={handleSave}
        className="mt-4 px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow hover:bg-blue-700"
      >
        Save
      </button>
    </div>
  );
}
